mapper.update:
UPDATE course
SET color = #{color}, modifyDate = now()
WHERE courseId = #{courseId}

mapper.select:
SELECT x, y
FROM course_detail
WHERE location = #{location}

SELECT COUNT(*) > 0
FROM course_detail
WHERE courseId = #{courseId} AND location = #{location}

SELECT COUNT(*) > 0
FROM course_detail
WHERE courseId = #{courseId} AND location = #{location}
