mapper.update:
UPDATE USER
SET MESSAGE = #{message},
    profileURL = #{profileURL}
WHERE userId = #{userId}

UPDATE COURSE
SET courseNAME = #{courseName},
    color = #{color},
    openClose = #{openClose}
WHERE userId = #{userId}
  AND courseId = #{courseId}

mapper.delete:
DELETE FROM COURSE
WHERE userId = #{userId}
  AND courseId = #{courseId}

DELETE FROM COURSE_DETAIL
WHERE userId = #{userId}
  AND course_detail_id = #{course_detail_id}

mapper.update:
UPDATE COURSE
    SET courseNAME = #{courseName},
        color = #{color},
        openClose = 0,
        bookMark = 1,
        myPinBoolean = 1,
        likeBoolean = 0,
        visitDate = #{visitDate}
    WHERE userId = #{userId}
      AND courseId = #{courseId};

UPDATE COURSE_DETAIL
    SET location = location,
        x = x,
        y = y,
        day = day
    WHERE userId = #{userId}
      AND courseId = #{courseId}
      AND course_detail_id = #{course_detail_id};
