mapper.insert:
INSERT INTO user(
      userId
    , nickname
    , userPwd
) values (
      #{userId}
    , #{nickname}
    , #{password}
)

mapper.select:
select count(*)
    from user
    where userId = #{userId}

select count(*)
    from user
    where nickname = #{nickname}

select userId
    , userPwd
    , nickname
    from user
    where userId = #{userId}
      and userPwd = #{password}
